import React, {useState, useEffect } from 'react';
import '../css/card.css';

import Axios from 'axios';



const Card = ({title, upTo}) => {


    const [taskList, saveTaskList] = useState([]);

    useEffect(()=>{
        Axios.get("http://localhost:3010/api/getAll").then((response)=>{
            console.log(response.data);
            saveTaskList(response.data);

            console.log(response.data.color);

            document.getElementById('body-card-color').style.backgroundColor = response.data.color;


        })
    }, []);


    return(
        <>
            { taskList.map((value)=>{
                return   
                <div className="body-card" id="body-card-color">
                    { document.getElementById('body-card-color').style.backgroundColor = value.color}
                <h2 className="title-card">{value.title}</h2>
                <hr className="border-card"></hr>
                <div className="content-card">
                    <form className="form-save">
                        <p>{value.content}</p>
                        {upTo ? <button className="btn-card">{upTo}</button> : null}
                        <button className="btn-card">Remove</button>
                    </form>
                </div>
            </div>
            })}
        </>
    );
}
export default Card;
