import React, {useState} from 'react';
import Error from './Error';
import Spinner from './Spinner';
import '../css/card.css';
import Axios from 'axios';


const FormCard = () => {


    const submitTask = () =>{
        Axios.post("http://localhost:3010/api/insert", {
            content: confirm
        }).then(()=>{
            alert('Success!');
        });
    }


    return(
        <>
            <div className="body-card" id="card-task-color">
                <h2 className="title-card">{'New task'}</h2>
                <hr className="border-card"></hr>
                <div className="content-card">
                    <form onSubmit = {sendContent} className="form-save">
                        <input className="no-style-input" id="card-input" type="text" placeholder="Write me" onChange={ e => saveContent(e.target.value)}></input>
                        <button className="btn-card">Save</button>
                    </form>
                </div>
                <div className="content-card ">
                    {(confirm) ? <p>{confirm} </p>: null}
                    <form className="form-save">
         {/*                <button className="btn-card">REMOVE</button> */}
                        <button onClick={submitTask} className="btn-card">OK !</button>
                    </form>
                </div>
                <input type="color" className="card-color-picker" onChange={ e => document.getElementById("card-task-color").style.backgroundColor = e.target.value}></input>
                {/* Conditional components */}
                { (error) ? <Error/> : null  }  
                { (load) ? <Spinner/> : null  } 
            </div>

        </>
    );
}
export default FormCard;






