import React, {useState} from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import Nav from './components/Nav';
import FormCard from './components/Form-card';
import Card from './components/Card';
import Home from './components/Home';
import './App.css';
import './css/utilities.css';


function App() {


  const handleChange = (e) =>{
    if(e.target.value === "1"){
      document.body.style.backgroundImage = "url('./img/background1')";
    } else if(e.target.value === "2"){
      document.body.style.backgroundImage = "url('https://cnnespanol.cnn.com/wp-content/uploads/2020/07/200703104728-labrador-retriever-stock-super-169.jpg?quality=100&strip=info')";
    } else if(e.target.value === "3"){
    document.body.style.backgroundImage = "url('https://cnnespanol.cnn.com/wp-content/uploads/2020/07/200703104728-labrador-retriever-stock-super-169.jpg?quality=100&strip=info')";
    }
    }

  return (
    <>  
      <Nav/> 
          <div className="new-button">
            <a className="new-link" href="formcard" >New +</a>
          </div>
          <div className="new-button ml-90p" >
            <form >Background
              {/*    <select onChange={(e) => setBackground(e.target.value)} onChange= {(e) => changebg} value={background}>
              */}

              <select onChange={handleChange} >     

                <option value= "1" >Value 1</option>
                <option value= "2" >Value 2</option>
                <option value= "3">Value 3</option>
              </select>
            </form>
          </div>
        <h2 className="text-control mt-5p">Welcome!</h2>  {/* New / To do / Doing / Done */}
        <div className="container-card">
          <BrowserRouter>
            <Switch>
            <Route path='/home' component={Home} />
              <Route path='/formcard' component={FormCard} />
              <Route path='/all' 
                component={() => (
                  <Card
                    title={'All'}
                    />
                  )}
                  />
              <Route path='/todo' 
                component={() => (
                  <Card
                    title={'To do'}  
                    upTo="Doing"
                    />
                  )}
                  />
              <Route path='/doing' 
                component={() => (
                  <Card
                    title={'Doing'}
                    upTo="Done"
                    />
                  )}
                  />
              <Route path='/done' 
                component={() => (
                  <Card
                    title={'Done'}  />
                  )}
                  />

            </Switch>
          </BrowserRouter>
        </div>
    </>
);
}


export default App;
