import React from 'react';
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom';
import './App.css';
import Nav from './components/Nav';
import FormCard from './components/Form-card';
import Card from './components/Card';
import './css/utilities.css';

function App() {
  return (
    <>  
      <Nav/> 
          <div className="new-button">
            <a className="new-link" href="formcard" >New +</a>
          </div>
        <h2 className="text-control mt-5p">Welcome!</h2>  {/* New / To do / Doing / Done */}
        <div className="container-card">
          <BrowserRouter>
            <Switch>

              <Route path='/formcard' component={FormCard} />
              <Route path='/todo/:date' breadcrumb="todo"
              title={DATE} component={Card} titulo="titulo"/>

            </Switch>
          </BrowserRouter>
        </div>
    </>
);
}
/* <input type="color"> */

export default App;
