import React, {useState} from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import Nav from './components/Nav';
import FormCard from './components/Form-card';
import Card from './components/Card';
import Home from './components/Home';
import './App.css';
import './css/utilities.css';

function App() {
  const imgs = [
     'https://source.unsplash.com/k3IogSsONd4/800x600',
     'https://source.unsplash.com/gThfDnqgfMw/800x600',
     'https://source.unsplash.com/_1x_x8Vtg2w/800x600',
     'https://source.unsplash.com/TFP_s4_jRuE/800x600',
     'https://source.unsplash.com/pElM4yerF5Q/800x600',
     'https://source.unsplash.com/sFsy8CKyQ5c/800x600',
     'https://source.unsplash.com/0WGucY1VHI0/800x600',
     'https://source.unsplash.com/1ciHU-qPifY/800x600',
     'https://source.unsplash.com/JZCJotPa96c/800x600',
  ]

  const [background, setBackground] = useState('https://source.unsplash.com/gThfDnqgfMw/800x600');



  const changebg = (e) =>{
    console.log(background);
    document.body.style.setBackgroundColor = "black";
  }
  return (
    <>  
      <Nav/> 
          <div className="new-button">
            <a className="new-link" href="formcard" >New +</a>
          </div>
          <div className="new-button ml-90p" >
            <form >Background
           {/*    <select onChange={(e) => setBackground(e.target.value)} onChange= {(e) => changebg} value={background}>
            */}
              <select onChange={(e) =>  changebg} value={background}>     
                <option value=  'https://source.unsplash.com/JZCJotPa96c/800x600' >Value 1</option>
                <option value=  'https://source.unsplash.com/JZCJotPa96c/800x600' >Value 2</option>
                <option value=  'https://source.unsplash.com/JZCJotPa96c/800x600'>Value 3</option>
              </select>
            </form>
          </div>
        <h2 className="text-control mt-5p">Welcome!</h2>  {/* New / To do / Doing / Done */}
        <div className="container-card">
          <BrowserRouter>
            <Switch>
            <Route path='/home' component={Home} />
              <Route path='/formcard' component={FormCard} />
              <Route path='/all' 
                component={() => (
                  <Card
                    title={'All'}
                    />
                  )}
                  />
              <Route path='/todo' 
                component={() => (
                  <Card
                    title={'To do'}  
                    upTo="Doing"
                    />
                  )}
                  />
              <Route path='/doing' 
                component={() => (
                  <Card
                    title={'Doing'}
                    upTo="Done"
                    />
                  )}
                  />
              <Route path='/done' 
                component={() => (
                  <Card
                    title={'Done'}  />
                  )}
                  />

            </Switch>
          </BrowserRouter>
        </div>
    </>
);
}


export default App;
