import '../css/card.css';

import Axios from 'axios';



const Card = ({title, upTo}) => {

    const intro = document.querySelector('#intro');
    const intro1 = document.querySelector('#intro1');
    const intro2 = document.querySelector('#intro2');
    const intro3 = document.querySelector('#intro3');
    const intro4 = document.querySelector('#intro4');
    const intro5 = document.querySelector('#total');

/* console.log(intro5);

intro5.style.backgroundColor(color);
 */


/* 
var intro = document.getElementById('intro');
intro.style.backgroundColor = '#FF00FF'; */

const [taskList, saveTaskList] = useState([]);

const [color, saveColor] = useState(null);


useEffect(()=>{
    Axios.get("http://localhost:3010/api/getAll").then((response)=>{
        console.log(response.data);
        saveTaskList(response.data);

        console.log(response.data.color);



    })
}, []);


return(
        <>
        <div id="total">
            { taskList.map((value)=>{
                /*                 { document.getElementById('body-card-color').style.backgroundColor = value.color} */
                return                   <div className="body-card" id="intro" >
                    saveColor(value.color)

                <h2 className="title-card" id="intro1">{value.title} </h2>
                <hr className="border-card" id="intro2"></hr>
                <div className="content-card" id="intro3">
                    <form className="form-save" id="intro4">
                        <p>{value.content}</p>
                        {upTo ? <button className="btn-card">{upTo}</button> : null}
                        <button className="btn-card">Remove</button>
                    </form>
                </div>
            </div>
            })}
       </div>
        </>
    );
}
export default Card;
